  if(!Product){
    return(
      <div>Product Does'nt Exist !</div>
    )
  }
    return (
        <div>
            <div className="col-2">
              <img className="large" src={Product.image} alt={Product.name}></img>
            </div>
            <div className="col-1"></div>
            <div className="col-1"></div>
        </div>
    )
}
